const path = 'https://coronavirus-19-api.herokuapp.com/countries'

const headers = {
  method: 'get',
  mode: 'cors',
  cache: 'default'
}

function Api(country){
  return fetch(`${path}/${country}`, headers)
  .then((response) => response.json())
}

export default {Api}
